import React, { useState } from 'react';
import axios from 'axios';
import './assets/login.css';

const LoginPage = (props) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('http://localhost:5000/funny-api/login', {
      email,
      password,
    })
      .then((response) => {
        localStorage.setItem('token', response.data.token);

      })
      .catch((error) => {
        console.log(error);
        setErrorMessage(error.response.data);
      });
  };

  return (
    <div className='flex w-screen h-screen'>
      <div className='w-[60%] h-full flex justify-center items-center'>
        <form onSubmit={handleSubmit}>
          <div className='login-form flex flex-col gap-[36px] justify-center items-center'>
            <div className='register-box-title mb-4'>
                    <h2>
                        Register box
                    </h2>
            </div>
            <div className='input-effect-div input-effect'>
                  <input
                      type="email"
                      name="email"
                      className="effect-20 placeholder:text-transparent"
                      value={email}
                      onChange={handleEmailChange}
                      required
                      placeholder='email'
                  />
                  <label>Your email</label>
                  <span className="focus-border">
                    <i></i>
                  </span>
            </div>
            <div className='input-effect-div input-effect'>
              <input
                type="password"
                name="password"
                className="effect-20 placeholder:text-transparent"
                value={password}
                onChange={handlePasswordChange}
                required
                placeholder='csac'
              />
                <label>Password</label>
                <span className="focus-border">
                  <i></i>
                </span>
            </div>
            <div className='flex justify-center items-center flex-col gap-3'>
              <button className='bg-[blue] hover:bg-[darkblue] rounded-[8px] outline-none border-none p-[12px] text-white w-full max-w-[200px]' type="submit">Register</button>
              <p>
                Still not have an account?  <a href="/register" className='underline text-[blue]'>Start your journey here!</a>
              </p>
            </div>
          </div>
        </form>
      </div>
      <div className='w-[40%] bg-sky-500 h-full'>

      </div>
      {errorMessage && <p>{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
