import React, { useState } from 'react';
import axios from 'axios';
import './assets/index.css'

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const [message, setMessage] = useState('')

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/funny-api/register', formData);
      console.log(response.data);
      setMessage(response.data.message)
    } catch (error) {
      console.log(error.response.data);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className='register-page'>
      <div className='register-introduce-box'>
        <h1>Register Page</h1>
      </div>
      <div className='register-box'>
        <form onSubmit={handleSubmit}>
            <div className='register-form'>
                <div className='register-box-title'>
                    <h2>
                        Register box
                    </h2>
                </div>
                <div className='input-effect-div input-effect'>
                <input
                    type="text"
                    name="username"
                    className="effect-20"
                    value={formData.username}
                    onChange={handleInputChange}
                    required
                    placeholder='username'
                />
                    <label>Your username</label>
                    <span className="focus-border">
                        <i></i>
                    </span>
                </div>
                <div className='input-effect-div input-effect'>
                    <input
                        type="email"
                        name="email"
                        className="effect-20"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                        placeholder='email'
                    />
                     <label>youremail@funnymovies.com</label>
                     <span className="focus-border">
                        <i></i>
                    </span>
                </div>
                <div className='input-effect-div input-effect'>
                    <input
                        type="password"
                        name="password"
                        className="effect-20"
                        value={formData.password}
                        onChange={handleInputChange}
                        required
                        placeholder='password'
                    />
                    <label>Your password</label>
                        <span className="focus-border">
                            <i></i>
                        </span>
                </div>
                <div className='flex justify-center items-center flex-col gap-3'>
                    <button type="submit">Register</button>
                    <p>
                        Already have an account?  <a href="/login" className='underline text-[blue]'>Login here!</a>
                    </p>
                </div>
            </div>
        </form>
      </div>
      {
        message !== '' && 

        <div className='register-overlay'>
            <div className='flex flex-col gap-3'>
                <div>
                    {       
                        message
                    }
                </div>
                <div className='flex gap-2'>
                    <button className='bg-transparent p-3 border rounded-sm'>Cancel</button>
                    <button className='bg-[blue] p-3 rounded-sm'>Login now</button>
                </div>
            </div>
        </div>

      }
    </div>
  );
};

export default RegisterPage;
