import React, { useState, useEffect } from "react";
import axios from "axios";
import './assets';

const VideoItem = (props) => {
  const [videoDetail, setVideoDetail] = useState(null);

  useEffect(() => {
    const fetchVideoData = async () => {
      await axios
        .get(`http://localhost:5000/video/${props.videoData.video_id}`)
        .then((response) => {
          setVideoDetail(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    };
    fetchVideoData();
  },[props]);

  console.log(videoDetail);
  const formatDescription = (content) => {
    // const formatedContent = content.replace(/\\/g, '&nbsp;').replace(/\n/g, '<br>');
    return  content
  }
  return (
    <div>
      {videoDetail && (
        <div className="grid grid-cols-2">
          <div></div>
          <div className="flex flex-col gap-5">
            <div>
              <a href={props.videoData.url}>
                <p className="text-[18px] text-[blue]">
                  {videoDetail.snippet.title}
                </p>
              </a>
            </div>
            <div className='flex gap-5'>
                <p className='text-[16px]'>
                  Shared by:  
                </p>
                <p className="text-[16px]">
                    {videoDetail.snippet.channelTitle}
                </p>
            </div>
            <div className='text-[14px] h-20 overflow-hidden text-ellipsis line-clamp-4'>
                {formatDescription(videoDetail.snippet.description)}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoItem;
