import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './assets/index.css'

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });
  const [message, setMessage] = useState(null)
  const [popUpMessage, setPopUpMessage] = useState(false)


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/funny-api/register', formData)
      localStorage.setItem('token', response.data.token)
      setMessage(response.data.message)
    } catch (error) {
      console.log(error.response.data);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const changePopUpState = () => {
    setPopUpMessage(true);
  }


  return (
    <div className='register-page'>
      <div className='register-introduce-box'>
        <h1>Register Page</h1>
      </div>
      <div className='register-box'>
        <form onSubmit={handleSubmit}>
            <div className='register-form'>
                <div className='register-box-title mb-4'>
                    <h2>
                        Register box
                    </h2>
                </div>
                <div className='input-effect-div input-effect'>
                <input
                    type="text"
                    name="username"
                    className="effect-20"
                    value={formData.username}
                    onChange={handleInputChange}
                    required
                    placeholder='username'
                />
                    <label>Your username</label>
                    <span className="focus-border">
                        <i></i>
                    </span>
                </div>
                <div className='input-effect-div input-effect'>
                    <input
                        type="email"
                        name="email"
                        className="effect-20"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                        placeholder='email'
                    />
                     <label>youremail@funnymovies.com</label>
                     <span className="focus-border">
                        <i></i>
                    </span>
                </div>
                <div className='input-effect-div input-effect'>
                    <input
                        type="password"
                        name="password"
                        className="effect-20"
                        autoComplete='true'
                        value={formData.password}
                        onChange={handleInputChange}
                        required
                        placeholder='password'
                    />
                    <label>Your password</label>
                        <span className="focus-border">
                            <i></i>
                        </span>
                </div>
                <div className='flex justify-center items-center flex-col gap-3'>
                    <button type="submit">Register</button>
                    <p>
                        Already have an account?  <a href="/login" className='underline text-[blue]'>Login here!</a>
                    </p>
                </div>
            </div>
        </form>
      </div>
      {/* {
        popUpMessage && 

        <div className='register-overlay'>
            <div className='grid bg-white w-[400px] min-h-[300px] justify-center items-center p-4 rounded-lg shadow-lg'>
                <div className='text-center text-[24px]'>
                    {
                        message
                    }
                </div>
                <div className='flex gap-3'>
                    <button onClick={setPopUpMessage(false)}  className='bg-transparent p-3 border rounded-md min-w-[150px] hover:bg-[#E8E8E8E8]'>Cancel</button>
                    <button  className='bg-[blue] p-3  border-none rounded-md text-white min-w-[150px] hover:bg-[darkblue]'>Login now</button>
                </div>
            </div>
        </div>

      } */}
      <div>{message}</div>
    </div>
  );
};

export default RegisterPage;
