import React, { useContext, useState }  from 'react';
import { RootStoreContext } from '../../store/rootStore';
import { observer } from "mobx-react";
import { toJS } from 'mobx';
import Header from "../../components/header/index";
import"./assets/index.css";



const SharingPage = observer(() => {
  const rootStore = useContext(RootStoreContext);

  const [videoTitle, setVideoTitle] = useState('');


  return (
    <div className='w-full h-full'>
      <Header></Header>
      <div className='w-full h-full flex justify-center items-center'>
        <div className='w-[500px] h-[300px] flex flex-col rounded-lg bg-white shadow-box-sharing py-[32px] px-[20px] justify-between'>
            <div>
                <p className='user-action-link font-mono text-[blue] font-bold text-[21px]'>Sharing a youtube video</p>
            </div>
            <div className='w-full flex flex-col gap-y-3'>
                <p className='text-[16px] font-mono'>
                    Your youtube video URL:
                </p>
                <input type="text" className='w-full outline-none rounded h-[40px] pl-3 border border-solid border-black focus:border-[blue] hover:border-[blue]'/>
            </div>
            <div className='w-full flex justify-center'>
                <button type={'submit'} className='bg-[blue] text-white hover:bg-[darkblue] w-6/12 rounded-md outline-none p-3'>
                    Share
                </button>
            </div>
        </div>
      </div>

    </div>
  );
});

export default SharingPage;
