import { createContext } from "react";
import { makeAutoObservable } from "mobx";


class RootStore {
  userInfo = null;

  constructor() {
    makeAutoObservable(this);
  }

  setUserInfo = (userInfo) => {
    this.userInfo = userInfo;
  };
}

export const RootStoreContext = createContext(new RootStore());
